<?php
/**
 * powerspeed_image
 *
 * Embeds images directly into the HTML to minimize HTTP requests with the cost of less efficient caching.
 * Called by the theme directly, not end users, although it is obviously a public function
 */
function powerspeed_image($image_to_output='', $image_width = null, $image_height = null) {
	$image_output = '<img src="';
	switch($image_to_output) {
		case 'flat-social-media-icons-deviantart':
			$image_alt = __('DeviantArt', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-email':
			$image_alt = __('Email', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-facebook':
			$image_alt = __('Facebook', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-flickr':
			$image_alt = __('Flickr', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-instagram':
			$image_alt = __('Instagram', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-linkedin':
			$image_alt = __('LinkedIn', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-pinterest':
			$image_alt = __('Pinterest', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-rss':
			$image_alt = __('RSS', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-share':
			$image_alt = __('Share', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-tumblr':
			$image_alt = __('Tumblr', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-twitter':
			$image_alt = __('Twitter', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-vimeo':
			$image_alt = __('Vimeo', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;
		case 'flat-social-media-icons-youtube':
			$image_alt = __('YouTube', 'powerspeed');
			$image_output .= 'data:image/png;base64,';
		break;

	}
	$image_output .= '" alt="" ';
	if($image_width != null) {
		$image_output .= ' width="'.$image_width.'" ';
	}
	if($image_height != null) {
		$image_output .= ' height="'.$image_height.'" ';
	}
	$image_output .= ' />';
	return $image_output;
}
